import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Breadcrumb, Card, Form } from 'react-bootstrap';
import './App.css';
import Axios from 'axios';
const routes = require('./routes');
const dotenv = require('dotenv');

dotenv.config();

function Restaurants() {

    const [restaurants, restaurantList] = useState([]);

    useEffect(() => {
        Axios.get(routes.listRestaurantsByName).then((result) => {
            restaurantList(result.data);
        })
    })

    return <div className="App">
        <h1>Restaurant App</h1>
        <Link to="/"><Button>Agregar Restaurantes
            </Button></Link>
        <Link to="/reservas"><Button>Reservas
            </Button></Link>

        <Card>
            <Card.Body>
                <div className='form'>
                    <Form.Label>Nombre del restaurante</Form.Label>
                    <Form.Control type='text' name='name' onChange={(e) => {
                        setRestaurantName(e.target.value)
                    }} />
                    <Form.Label>Fecha de la reserva</Form.Label>
                    <Form.Control type='date' name='date' onChange={(e) => {
                        setReserveDate(e.target.value)
                    }} />

                    <Button onClick={submitRestaurant} >Reservar</Button>
                    <h1>Restaurantes: </h1>
                    {restaurants.map((val) => {
                        return <div className="card">
                            {val.restaurantName} Ciudad
                        </div>
                    })}
                </div>
            </Card.Body>
        </Card>
    </div>;
}

export default Restaurants;
