            <Form.Label>Buscar Restaurante</Form.Label>
            <Form.Control type='text' name='search' placeHolder="Nombre" onChange={(e) => { }} />
            <Button>INFO</Button>
            <div class="divider" />
            <Button>EDIT</Button>

        </div>
    )
};


